import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/styles";
import Styles from "./styles";

class Milestones extends Component {
	constructor(props) {
		super(props);
		this.state = {
			activeStep: 0,
			nestedActiveStep: 0,
			milestones: [
				{
					id: 0,
					title: "hey",
					description: "hello",
					deadline: "21-08-2019",
					submissions: [
						{
							id: 0,
							summary: "hey",
							file: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "rejected",
							remark: "could have done better"
						},
						{
							id: 1,
							summary: "hey",
							file: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "bgfh",
							remark: "could have done better"
						}
					]
				},
				{
					id: 1,
					title: "hey",
					description: "hello",
					deadline: "21-08-2019",
					submissions: [
						{
							id: 0,
							summary: "hey",
							file: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "approved",
							remark: "could have done better"
						},
						{
							id: 1,
							summary: "hey",
							file: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "rejected",
							remark: "could have done better"
						}
					]
				}
			]
		};
	}

	handleNext = () => {
		this.setState(prevState => {
			return {
				nestedActiveStep: prevState.nestedActiveStep + 1
			};
		});
	};

	handleBack = () => {
		this.setState(prevState => {
			return {
				activeStep: prevState.activeStep - 1
			};
		});
	};
	handleNextMilestone = () => {
		console.log("hey yo yo");
		this.setState(prevState => {
			return {
				activeStep: prevState.activeStep + 1,
				nestedActiveStep: 0
			};
		});
	};

	handleReset = () => {
		this.setState({
			activeStep: 0
		});
	};

	render() {
		return (
			<div className={this.props.classes.root}>
				<div className={this.props.classes.heading}>
					<Typography variant="h4">Milestones</Typography>
				</div>
				<Stepper activeStep={this.state.activeStep} orientation="vertical">
					{this.state.milestones.map((milestone, index) => (
						<Step key={milestone.id}>
							<StepLabel>{milestone.title}</StepLabel>
							<StepContent>
								<Typography>{milestone.description}</Typography>
								<Typography>Deadline : {milestone.deadline}</Typography>
								<Stepper
									activeStep={this.state.nestedActiveStep}
									orientation="vertical">
									{milestone.submissions.map(submission => (
										<Step key={submission.id}>
											<StepLabel>
												{submission.summary}
												{submission.file}
												{submission.url}
											</StepLabel>
											<StepContent>
												{submission.status === "rejected" ||
												submission.status === "approved" ? (
													<div>
														<Typography>{submission.remark}</Typography>
														<div
															className={this.props.classes.actionsContainer}>
															<div>
																<Button
																	variant="contained"
																	color="primary"
																	onClick={this.handleNext}
																	className={this.props.classes.button}>
																	Next
																</Button>
															</div>
														</div>
													</div>
												) : (
													<div className={this.props.classes.actionsContainer}>
														<div>
															<Button
																onClick={this.handleNext}
																className={this.props.classes.button}>
																Reject
															</Button>
															<Button
																variant="contained"
																color="primary"
																onClick={this.handleNext}
																className={this.props.classes.button}>
																Approve
															</Button>
														</div>
													</div>
												)}
											</StepContent>
										</Step>
									))}
									{this.state.nestedActiveStep === milestone.submissions.length
										? this.handleNextMilestone()
										: null}
								</Stepper>
							</StepContent>
						</Step>
					))}
				</Stepper>
				{this.state.activeStep === this.state.milestones.length && (
					<Paper
						square
						elevation={0}
						className={this.props.classes.resetContainer}>
						<Typography>
							All milestones completed - you&apos;re finished
						</Typography>
						<Button
							onClick={this.handleReset}
							className={this.props.classes.button}>
							Reset
						</Button>
					</Paper>
				)}
			</div>
		);
	}
}

export default withStyles(Styles)(Milestones);
