import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import QuizToolbar from "./components/QuizToolbar";
import CreateQuiz from "./components/CreateQuiz";
import Loader from "./components/loader";
import { fetchQuizlist } from "api/quizzes";
import MaterialTable from "material-table";
import { connect } from "react-redux";
import { Quiz } from "serializers/quiz";
import Tooltip from "@material-ui/core/Tooltip";
import swal from "sweetalert";
import { Typography } from "@material-ui/core";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class QuizList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false,
			quizlistTabledata: [],
			isLoading: true
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		fetchQuizlist(this.props.token)
			.then(data => {
				let quizlistTabledata = data.quizes.map(quiz => {
					return new Quiz(quiz);
				});

				// push data to redux
				this.props.pushQuizListdata(quizlistTabledata);

				this.setState({
					// quizlistTabledata: quizlistTabledata,
					isLoading: false
				});
			})
			.catch(error => {
				swal({
					title: "Internal Server Error",
					text: "Please Try Refreshing Page",

					button: "Ok"
				});
			});
	};

	handleClickOpen = () => {
		this.setState({
			open: true
		});
	};
	handleClose = () => {
		this.setState({
			open: false
		});
	};

	handleEditQuiz = id => {
		this.props.history.push("/quiz/" + id + "/edit-quiz");
	};
	handleScheduleQuiz = id => {
		// push row data to redux store

		this.props.history.push("/quiz/" + id + "/schedule-quiz");
	};

	columns = [
		{
			title: "Title",
			field: "title",
			cellStyle: {
				"font-size": "15px"
			}
		},
		{
			title: "Skills",
			field: "skills",
			cellStyle: {
				"font-size": "15px"
			},
			render: rowData =>
				rowData.skills.map(skill => {
					return <li>{skill.tag}</li>;
				})
		},
		{
			title: "Number Of Questions",
			field: "numberOfQuestions",
			cellStyle: {
				"font-size": "15px"
			},
			type: "numeric"
		},
		{
			title: "Scheduling Status",
			field: "schedulingStatus",
			cellStyle: {
				"font-size": "15px"
			},
			lookup: {
				0: "Not Scheduled",
				1: "Scheduled",
				2: "Disabled",
				3: "Expired"
			}
			// render: rowData => {
			// 	if (rowData.schedulingStatus == 1) return <p>{rowData.schedule}</p>;
			// }
		},
		{
			title: "Quiz Date",
			field: "sechedule",
			cellStyle: {
				"font-size": "15px"
			},
			render: rowData => {
				if (rowData.schedulingStatus == 1) return <p>{rowData.schedule}</p>;
			}
		},

		{
			title: "Id",
			field: "id",
			hidden: true
		}
	];

	data = [
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 1,
			schedulingStatus: true,
			id: "1"
		},
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 5,
			schedulingStatus: false,
			id: "2"
		},
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 1,
			schedulingStatus: true,
			id: "3"
		},
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 1,
			schedulingStatus: false,
			id: "4"
		}
	];

	actions = [
		{
			icon: "edit",
			tooltip: "Edit Content",
			onClick: (event, rowData) => {
				return this.handleEditQuiz(rowData.id);
			}
		},
		rowData => ({
			icon: "schedule",
			tooltip: "Schedule Quiz",
			onClick: (event, rowData) => {
				return this.handleScheduleQuiz(rowData.id);
			},
			disabled: rowData.numberOfQuestions < 5
		})
	];

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Quizzes">
				<div className={classes.root}>
					<QuizToolbar handleClickOpen={this.handleClickOpen} />
					{this.state.open && (
						<CreateQuiz open={this.state.open} handleClose={this.handleClose} />
					)}
					<div className={classes.content}>
						{!this.state.isLoading ? (
							<MaterialTable
								columns={this.columns}
								data={this.props.quizlistTabledata}
								options={{
									filtering: true,
									actionsColumnIndex: -1,
									headerStyle: {
										"font-size": "18px"
									}
								}}
								actions={this.actions}
								title={
									<Typography
										style={{ "font-size": "18px", fontWeight: "bolder" }}>
										Quiz List
									</Typography>
								}
							/>
						) : (
							<Loader />
						)}
					</div>
				</div>
			</DashboardLayout>
		);
	}
}
const mapStateToProps = state => {
	return {
		token: state.auth.token,
		quizlistTabledata: state.quiz.quizlistTabledata
	};
};

const mapDispatchToProps = dispatch => {
	return {
		pushQuizListdata: data =>
			dispatch({ type: "PUSH QUIZLIST DATA", data: data })
	};
};

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(withStyles(styles)(QuizList));
