import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";

// Externals
import classNames from "classnames";
import PropTypes from "prop-types";

// Shared services
import { getProducts } from "services/product";

// Material helpers
import { withStyles } from "@material-ui/styles";
import Chip from "@material-ui/core/Chip";

// Material components
import {
	Button,
	IconButton,
	Typography,
	CircularProgress
} from "@material-ui/core";

// Material icons
import {
	ArrowRight as ArrowRightIcon,
	MoreVert as MoreVertIcon
} from "@material-ui/icons";

// Shared components
import {
	Portlet,
	PortletHeader,
	PortletLabel,
	PortletContent,
	PortletFooter
} from "components";

// Component styles
import styles from "./styles";

class Alerts extends Component {
	signal = true;

	state = {
		isLoading: false,
		limit: 4,
		products: [],
		productsTotal: 0,
		error: null
	};

	render() {
		const { classes, className, ...rest } = this.props;
		const { productsTotal } = this.state;

		const rootClassName = classNames(classes.root, className);

		return (
			<Portlet {...rest} className={rootClassName}>
				<PortletHeader noDivider>
					<PortletLabel title="Alerts" />
				</PortletHeader>
				<PortletContent className={classes.portletContent}>
					<Typography>Weaknesses</Typography>
					<div className={classes.product}>
						<div className={classes.productDetails}>
							{this.props.weaknesses.map(weakness => {
								<Chip label={weakness} className={classes.chip} />;
							})}
						</div>
					</div>
					<Typography>Over Due Milestones</Typography>
					<div className={classes.product}>
						<div className={classes.productDetails}>
							<Link to="#">
								<Typography className={classes.productTitle} variant="h5">
									hgchgch
								</Typography>
							</Link>
							<Typography className={classes.productTimestamp} variant="body2">
								Updated 5hr ago
							</Typography>
						</div>
					</div>
				</PortletContent>
			</Portlet>
		);
	}
}

Alerts.propTypes = {
	className: PropTypes.string,
	classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Alerts);
