import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/styles";
import Styles from "./styles";
import MaterialTable from "material-table";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Icon from "@material-ui/core/Icon";
import IconButton from "@material-ui/core/IconButton";
import { Visibility } from "@material-ui/icons";
import { CloudDownload } from "@material-ui/icons";
import { Check } from "@material-ui/icons";
import { Cancel } from "@material-ui/icons";
import Grid from "@material-ui/core/Grid";
import DeleteIcon from "@material-ui/icons/Delete";

class Milestones extends Component {
	constructor(props) {
		super(props);
		this.state = {
			activeStep: 0,
			nestedActiveStep: 0,
			milestones: [
				{
					id: 0,
					title: "hey",
					description: "hello",
					deadline: "21-08-2019",
					submissions: [
						{
							id: 0,
							summary: "hey",
							fileurl: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "Rejected",
							remark: "could have done better"
						},
						{
							id: 1,
							summary: "hey",
							fileurl: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "Pending",
							remark: "could have done better"
						}
					]
				},
				{
					id: 1,
					title: "hey",
					description: "hello",
					deadline: "21-08-2019",
					submissions: [
						{
							id: 0,
							summary: "hey",
							fileurl: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "Approved",
							remark: "could have done better"
						},
						{
							id: 1,
							summary: "hey",
							fileurl: "https://material-ui.com/components/steppers/",
							url: "https://material-ui.com/components/steppers/",
							status: "Pending",
							remark: "could have done better"
						}
					]
				}
			]
		};
	}

	remark = () => {
		console.log("hey give remark ");
	};

	columns = [
		{ title: "id", field: "id", hidden: true },
		{ title: "Summary", field: "summary" },
		{
			title: "Status",
			field: "status",
			render: rowData => {
				if (rowData.status === "Pending") {
					return (
						<div>
							<Button
								onClick={this.remark}
								className={this.props.classes.button}>
								Reject
							</Button>
							<Button
								variant="contained"
								color="primary"
								onClick={this.remark}
								className={this.props.classes.button}>
								Approve
							</Button>
						</div>
					);
				} else {
					return rowData.status + " -" + rowData.remark;
				}
			}
		},
		{ title: "Remark", field: "remark", hidden: true },
		{
			title: "Url",
			field: "url",
			hidden: true
		},
		{
			title: "FileUrl",
			field: "fileurl",
			hidden: true
		}
	];

	actions = [
		{
			icon: "visibility",
			tooltip: "View Submission url",
			onClick: (event, rowData) => {
				var win = window.open(rowData.url, "_blank");
				win.focus();
			}
		},
		{
			icon: "cloud_download",
			tooltip: "Download Submitted file",
			onClick: (event, rowData) => {
				var win = window.open(rowData.fileurl, "_blank");
				win.focus();
			}
		}
	];

	handleNext = () => {
		this.setState(prevState => {
			return {
				activeStep: prevState.activeStep + 1
			};
		});
	};

	handleBack = () => {
		this.setState(prevState => {
			return {
				activeStep: prevState.activeStep - 1
			};
		});
	};

	render() {
		return (
			<div className={this.props.classes.root}>
				<div className={this.props.classes.heading}>
					<Typography variant="h4">Milestones</Typography>
				</div>
				<Stepper activeStep={this.state.activeStep} orientation="vertical">
					{this.state.milestones.map((milestone, index) => (
						<Step key={milestone.id}>
							<StepLabel>{milestone.title}</StepLabel>
							<StepContent>
								<Typography>{milestone.description}</Typography>
								<Typography>Deadline : {milestone.deadline}</Typography>
								{/* <MaterialTable
									columns={this.columns}
									data={milestone.submissions}
									options={{
										actionsColumnIndex: -1,
										search: false
									}}
									actions={this.actions}
									title="Submission Log"
								/> */}
								<Grid container spacing={2}>
									{milestone.submissions.map(submission => {
										return (
											<Grid
												item
												md={6}
												className={this.props.classes.splitFormSpacing}>
												<Card className={this.props.classes.card}>
													<CardContent>
														<Typography
															variant="body2"
															style={{ color: "black" }}
															component="p">
															{submission.summary}
														</Typography>
														<br />
														<Typography variant="h5" component="h2">
															Status: {submission.status}
														</Typography>
														<br />
														{submission.status === "Pending" ? (
															<div>
																<IconButton
																	onClick={this.remark}
																	className={this.props.classes.button}>
																	<Check />
																</IconButton>
																<IconButton
																	variant="outlined"
																	color="primary"
																	onClick={this.remark}
																	className={this.props.classes.button}>
																	<Cancel />
																</IconButton>{" "}
															</div>
														) : (
															<Typography variant="body2" component="p">
																Remark: {submission.remark}
															</Typography>
														)}
													</CardContent>
													<CardActions>
														<IconButton
															className={this.props.classes.button}
															aria-label="Download file">
															<CloudDownload />
														</IconButton>
														<IconButton
															className={this.props.classes.button}
															aria-label="Delete">
															<Visibility />
														</IconButton>
													</CardActions>
												</Card>
											</Grid>
										);
									})}
								</Grid>
								<div className={this.props.classes.actionsContainer}>
									<div>
										<Button
											disabled={this.state.activeStep === 0}
											onClick={this.handleBack}
											className={this.props.classes.button}>
											Back
										</Button>
										<Button
											variant="contained"
											color="primary"
											onClick={this.handleNext}
											className={this.props.classes.button}>
											{this.state.activeStep ===
											this.state.milestones.length - 1
												? "Finish"
												: "Next"}
										</Button>
									</div>
								</div>
							</StepContent>
						</Step>
					))}
				</Stepper>
				{this.state.activeStep === this.state.milestones.length && (
					<Paper
						square
						elevation={0}
						className={this.props.classes.resetContainer}>
						<Typography>
							All milestones completed - you&apos;re finished
						</Typography>
					</Paper>
				)}
			</div>
		);
	}
}

export default withStyles(Styles)(Milestones);
